INSERT INTO `stretchy_report` (`id`,`report_name`, `report_type`, `report_subtype`, `report_sql`, `description`, `core_report`, `use_report`) VALUES (188,'Loan Approved - API', 'API', 'Triggered', 'SELECT loan.`id` as loanId,loan.`loan_officer_id` as officerId,loan.`principal_amount_proposed` as loanAmount,loan.`submittedon_date`,loan.`approvedon_date`, loan.`disbursedon_date`, c.display_name as clientDisplayName, c.firstname as clientFirstName, loan.annual_nominal_interest_rate as annualInterestRate, s.firstname as officerFirstname, s.display_name as officerDisplayName FROM `m_loan` loan join m_client c on c.id = loan.client_id left join m_staff s on s.id = loan.loan_officer_id where loan.id = ${loanId} or ${loanId} = -1', 'Loan and client data of approved loan', '0', '0');
