INSERT INTO `stretchy_report` (`id`,`report_name`, `report_type`, `report_subtype`, `report_sql`, `description`, `core_report`, `use_report`) VALUES (191,'Savings Account Created - API', 'API', 'Triggered', 'SELECT account.`id` as accountId,account.`account_no` as accountNumber,account.`nuban_account_number` as nubanAccountNumber, account.`submittedon_date`, c.display_name as clientDisplayName, c.firstname as clientFirstName FROM `m_savings_account` account join m_client c on c.id = account.client_id where account.id = ${savingsId} or ${savingsId} = -1', 'Savings account and client data of created account', '0', '0');
INSERT INTO `stretchy_report` (`id`,`report_name`, `report_type`, `report_subtype`, `report_sql`, `description`, `core_report`, `use_report`) VALUES (192,'Savings Account Deposit - API', 'API', 'Triggered', 'SELECT transaction.`id` as transactionId,account.`account_no` as accountNumber,account.`nuban_account_number` as nubanAccountNumber, transaction.`amount` as depositAmount, transaction.`transaction_date` as transactionDate, transaction.`running_balance_derived` as accountBalance, c.display_name as clientDisplayName, c.firstname as clientFirstName FROM `m_savings_account` account join m_client c on c.id = account.client_id join m_savings_account_transaction `transaction` on account.id = transaction.savings_account_id where transaction.id = ${savingsTransactionId} or ${savingsTransactionId} = -1', 'Savings transaction and client data of savings account', '0', '0');
